pragma solidity ^0.4.25;

contract Airlines {

    mapping (address => Airline) internal airlines;
    uint internal registeredAirlineCount = 0;

    struct Airline {
        AirlineStatus status;
        address[] votes;
        uint funds;
    }

    enum AirlineStatus {
        Unregistered,
        Registered,
        Funded
    }

    // modifiers
    modifier requireAirlineRegistered(address airlineAddress) {
        require(airlines[airlineAddress].status == AirlineStatus.Registered, "Airline must be registered");
        _;
    }

    modifier requireAirlineFunded(address airlineAddress) {
        require(airlines[airlineAddress].status == AirlineStatus.Funded, "Airline must be funded");
        _;
    }

    // internal functions
    function add(address airlineAddress) internal {
        airlines[airlineAddress] = Airline(
            AirlineStatus.Unregistered, // default unregistered
            new address[](0), // no votes
            0 // default no funding
        );
    }

    function register(address airlineAddress) internal {
        add(airlineAddress);
        airlines[airlineAddress].status = AirlineStatus.Registered;
        registeredAirlineCount++;
    }

    function vote(address airlineAddress, address voterAddress) internal requireAirlineRegistered(airlineAddress) {
        airlines[airlineAddress].status = AirlineStatus.Registered;

    }

    function fund(address airlineAddress, uint256 fundingAmount) internal requireAirlineRegistered(airlineAddress) {
        airlines[airlineAddress].funds += fundingAmount;
        airlines[airlineAddress].status = AirlineStatus.Funded;
    }
}
